import { toast } from 'react-toastify';


export const errorNoti = (message, options = {
  position: "top-center",
  closeOnClick: false,
}) => {
  toast.error(message, options)
}

export const successNoti = (message) => {
  toast.success(message, {
    position: "top-center",
    closeOnClick: false,
  })
}
